import React, { Component } from 'react';
import './index.scss';

export default class Demo2048 extends Component{
    constructor(props){
        super(props)
        this.data = null;
        this.ctx = null;
        this.width = null;
        this.height = null;
        this.box_width = null;
        this.margin_width = null;
        this.digital = [];
    }
    componentDidMount(){
        this.ctx = this.container.getContext('2d');

        this.width = this.ctx.canvas.width;
        this.height = this.ctx.canvas.height;

        this.box_width = this.width*0.8*0.25;
        this.margin_width = this.width*0.2*0.20;

        this.getDigital();

    }

    getDigital = () =>{
        for(let i=0; i < 4; i++){
            this.digital[i] = [];
            for (let j = 0; j < 4; j++){
                this.digital[i][j] = 0;
            }
        }
    }


    render(){
        return(
            <canvas ref={el => this.container = el} style={{width: 500, height: 500}}></canvas>
        )
    }
}
