import React, { Component } from 'react';
import Ima1 from './1.jpg';

const Block = (canvas, left, top,avW, avH) => {
    this.canvas = canvas;
    this.left = left;
    this.top = top;
    this.avW = avW;
    this.avH = avH;
    this.init();
}
Object.assign(Block.prototype,{
    init: () => {
        this.canvas.style.position = 'absolute';
        this.canvas.style.left = this.avW * this.left + "px";
        this.canvas.style.top = this.avH * this.top + "px";
        this.canvas.x = this.left;
        this.canvas.y = this.top;

    }
})

export default class DemoPinTu extends Component{
    constructor(props){
        super(props)
        this.ctx = null;
        this.blocks = [];
        this.avw = null;
        this.avH = null;
        this.number = 3;
    }
    componentDidMount(){
        this.ctx = this.container.getContext('2d');
        let img = new Image();
        img.onload = () => {
            this.ctx.drawImage(img, 0, 0, 500, 500);
        }
        img.src = Ima1;

        this.clipImage();
    }

    clipImage = () => {
        this.avW = this.container.width / this.number;
        this.avH = this.container.height / this.number;

        for(let i = 0; i < this.number; i++){
            for (let j = 0; j < this.number; j++){
                this.blocks[i] = this.blocks[i] || [];
                let canvas = document.createElement("canvas");
                canvas.width = this.avW;
                canvas.height = this.avH;
                canvas.x = j;
                canvas.y = i;
                canvas.map = i + '_' + j;
                canvas.correctMap = i + '_' + j;
                let imageData = this.ctx.getImageData(j * this.avW, i * this.avH, this.avW, this.avH)
                canvas.getContext("2d").putImageData( imageData, 0, 0 );
                if( i === j && j=== (this.number-1) )break;
                // 把canvas放到二维数组blocks中;
                this.blocks[i][j] =  canvas;
            }
        }

        console.log("djdhdhdhdhdh", this.blocks);

        this.renderToDom();
    }
    renderToDom = () =>{

    }

    render(){
        return(<div ref={el => this.container = el} style={{width: 500, height: 500}}></div>)
    }
}
