import React, { Component } from 'react';
import Ima1 from './1.jpg';

export default class DemoPinTu extends Component{
    constructor(props){
        super(props)
        this.ctx = null;
        this.blocks = [];
        this.avw = null;
        this.avH = null;
        this.number = 3;
    }
    componentDidMount(){
        this.ctx = this.container.getContext('2d');
        let img = new Image();
        img.onload = () => {
            this.ctx.drawImage(img, 0, 0, 500, 500);
        }
        img.src = Ima1;

        this.clipImage();
    }

    clipImage = () => {
        this.avw = this.container.width / this.number;
        this.avH = this.container.height / this.number;

        console.log("2222333455", this.avW, this.avH);

    }

    render(){
        return(<canvas ref={el => this.container = el} width="500" height="500"></canvas>)
    }
}
