import React, { Component } from 'react';

const d3 = window.d3;

export default class D3Test extends Component{

    componentDidMount(){

        let width = 800, height = 800;

        const mapColor = '#114a93';
        const mapLineColor = '#0d67c7';

        var svg = d3.select('.container')
            .append('svg')
            .attr('width', width + 'px')
            .attr('height', height + 'px')

        var projection = d3.geoMercator()
            .center([107, 31])
            .scale(850)

        var path = d3.geoPath()
            .projection(projection);
        var mapLayer = svg.append('g')
            .attr('class', 'maplayer')
        fetch('geojson/CHN.json').then((response) => {
            response.json().then((res) => {
                mapLayer.selectAll('path').data(res.features).enter()
                .attr('d', path)
                .attr('vector-effect', 'non-scaling-stroke')
                .style('fill', function() {return "#44aaee"})
            })
        })
    }

    render(){
        return(
            <div className='container' style={{width: 800, height: 800, background: '#fff'}}>

            </div>
        )
    }
}
