import React, { Component } from 'react';
import Ima1 from './1.jpg';

export default class DemoPinTu extends Component{
    constructor(props){
        super(props)
        this.ctx = null;
    }
    componentDidMount(){
        this.ctx = this.container.getContext('2d');
        let img = new Image();
        img.src = Ima1;
        img.onload = () => {
            let ptrn = this.ctx.createPattern(img, 'no-repeat')
            this.ctx.fillStyle = ptrn;
            this.ctx.fillRect(0, 0, 500, 500);
        }
    }
    render(){
        return(<canvas ref={el => this.container = el} width="500" height="500"></canvas>)
    }
}
