import React, { Component } from 'react';
import './index.scss';
import Image1 from './images/event_1_large.jpg';
import Image2 from './images/event_2_large.jpg';
import Image3 from './images/event_3_large.jpg';
import Image4 from './images/event_4_large.jpg';
import Image5 from './images/event_5_large.jpg';

export default class Banner extends Component{
    constructor(props){
        super(props);
        this.swiper = null;
    }

    componentDidMount(){

        const Swiper = window.Swiper;

        this.swiper = new Swiper('.swiper-container', {
            autoplay: true,//可选选项，自动滑动
        })
    }

    render(){
        return(
            <div className="apple-retai">
                <div className="swiper-container">
                    <div className="swiper-wrapper">
                        <div className="swiper-slide init">
                            <div className="inner">
                            <figure><img src={Image1} /></figure>
                            <div className="gallery-item-desc">
                                <h3 className="typography-label">灵感迸发，一课又一课。</h3>
                                <p className="typography-body">参加我们的实际操作互动课程，与影响广泛的艺术家、音乐家、开发者和企业家开展交流。</p>
                            </div>
                            </div>
                        </div>
                        <div className="swiper-slide">
                            <div className="inner">
                            <figure><img src={Image2} /></figure>
                            <div className="gallery-item-desc">
                                <h3 className="typography-label">照片和视频</h3>
                                <p className="typography-body">探索如何拍摄出你乐于分享的照片和视频。这里有“光影漫步”、“光影实验室”、“Pro 行家讲座”和“进阶讲座”等各种课程等你参加。</p>
                            </div>
                            </div>
                        </div>
                        <div className="swiper-slide">
                            <div className="inner">
                            <figure><img src={Image3} /></figure>
                            <div className="gallery-item-desc">
                                <h3 className="typography-label">音乐</h3>
                                <p className="typography-body">你可以参加“进阶讲座”和“音乐实验室”等课程，开始创作属于自己的节拍；也可以参加“Pro 行家讲座”课程，让自己的音乐制作能力更进一步。</p>
                            </div>
                            </div>
                        </div>
                        <div className="swiper-slide">
                            <div className="inner">
                            <figure><img src={Image4} /></figure>
                            <div className="gallery-item-desc">
                                <h3 className="typography-label">编程</h3>
                                <p className="typography-body">来编写你的第一行代码，这比你想象的要容易多了。我们有“课外一小时”、“夏令营”、“进阶讲座”等各种课程，来教你学习如何编程。</p>
                            </div>
                            </div>
                        </div>
                        <div className="swiper-slide">
                            <div className="inner">
                            <figure><img src={Image5} /></figure>
                            <div className="gallery-item-desc">
                                <h3 className="typography-label">艺术和设计</h3>
                                <p className="typography-body">参加我们的“现场艺术创作”、“户外写生”、“课外一小时”和“进阶讲座”等课程，开始你的新创作。</p>
                            </div>
                            </div>
                        </div>
                    </div>
                    <div className="swiper-button-prev"></div>
                    {/* <!--左箭头--> */}
                    <div className="swiper-button-next"></div>
                    {/* <!--右箭头-->  */}
                </div>
            </div>
        )
    }
}
